insert  into `food`(`id`,`name`,`price`,`foodDesc`,`url`) values (1,'汽锅鸡',68.8,'汽锅鸡是云南的名菜之一，在汽锅下放一盛满水的汤锅，然后把鸡块放入汽锅内，纯由蒸汽将鸡蒸熟。保持了原汁，肉嫩香，汤清鲜。汽锅常以三七、虫草加入，有滋补之效。','../../images/food/41907383428d4ae6847979470b90a5591584594941193.jpg'),(2,'腾冲炒饵块',18.8,'久负盛名的皇封名吃“大救驾”——腾冲炒饵块，更是以其色、香、味、形四美并具的独有品质倾倒了有此口福的天下吃客。腾冲人将大米精加工为饵丝、饵块，已至少有三五百年历史。','../../images/food/7279048251b6479db925f834f66dcd901584594974238.jpg'),(3,'乌鸡汤',28.8,'以乌鸡、阿胶、黄精、桂圆、红枣、枸杞等食材制作而成。乌鸡营养丰富，并含有激素和紫色素，对人体白血球和和血色素有增强的作用。','../../images/food/e1cf1a9bad7d49c79dbec8fe3a394c331584594986943.jpg'),(4,'紫米八宝饭',16.5,'一道不折不扣的美容饭。以紫米为主要原料制成，是墨江、思茅、昆明一带独特的风味小吃，也是酒席上的名贵甜甜食。','../../images/food/1f17a9931d6f4b75b67f24a4c1f4da721584595004311.jpg'),(5,'昆明豌豆粉',16.5,'豌豆粉是昆明传统的汉族小吃，用干豌豆粒，磨瓣去皮，用水泡发后，掺水磨成浆，经过滤、熬者成糊，冷却后凝回而成。','../../images/food/dd3032a7236d4f8297659192bbd2cb881584595016425.jpg'),(6,'过桥米线',23.8,'过桥米线是云南美食的一张名片，也是外地游客来滇的必尝之物。要吃到一碗好的过桥米线，精髓在于汤、米线及配料。一根根米线上附着油脂，入口爽滑，劲道。','../../images/food/0eb1b76f7cdb46dc8f03a42c6828f8761584595051953.jpg'),(7,'老奶洋芋',19.8,'老奶洋芋是云南美食之一，它口感绵密，十分酥软，故得此名。制作简单，色泽金黄诱人，散发着阵阵土豆味儿，吃起来确实很香，还可以添加别的各种配料来丰富口感。','../../images/food/cb2f791adca0442abdfa7e0bbf2cece41584595082431.jpg'),(8,'红油猪耳',28.8,'红油猪耳是用猪耳制作的一道家常菜。猪耳含有蛋白质、脂肪、碳水化合物、维生素及钙、磷、铁等，具有补虚损、健脾胃的功效，适用于气血虚损、身体瘦弱者食用。','../../images/food/c45a45ef1b424debb499c918dfd192231584595094230.jpg'),(9,'青椒松茸',16.8,'昆明名菜之一。用青辣椒加蒜片与松茸片相炒，以肉汤加湿淀粉勾芡，百种缀红绿，清香味醇厚。','../../images/food/e3bb4a61e56541a6a064fa25674464591584595137975.jpg'),(10,'香竹饭',19.8,'香竹饭具有浓郁的傣族风味，每年11月至次年2月间做出的饭最好。此时竹子内有一层香气扑鼻的香膜，所以叫香竹。','../../images/food/a9c3dfb63b4343dabb4e7295fde8eeee1584595166747.jpg'),(11,'大救驾',22.5,'大救驾是云南腾冲县最出名的小吃之一，是炒饵块的一种，制作的方法是将饵块切成小片，再加上火腿、鸡蛋、肉、萝卜、番茄等，一起放在锅中爆炒而成。','../../images/food/b3bb00ecf00845d3b032c66c76dc06ac1584595180600.jpg'),(12,'烧饵块',8.8,'烧饵块是云南当地的一种民俗小吃。是用煮熟的大米饭压成块状，通常是圆形薄饼状。在炭火上面烤制，然后涂上酱料和菜馅即可。大米的清香搭配酱料的风味，是当地人都喜爱的可口食品。','../../images/food/b152616fc68f42dcb2183bf44a9fbaf31584595195950.jpg'),(13,'豆焖饭',13.8,'豆焖饭是选用刚摘下的鲜嫩蚕豆，剥壳去皮留下豆米，熟火腿切成指甲大小的片，再把上米掺入二分之一糯米掏洗干净，煮熟控出米汤。','../../images/food/3686cb748cdd4de596b0e4235f8a59d81584595219059.jpg'),(14,'无量翅包饭',9.8,'无量山翅包饭在大理当地备受欢迎，目前是大理最受欢迎的休闲小吃，全球各地到大理旅行的游客，无不一品号称大理十大美食之首的翅包饭为荣。','../../images/food/c0becc80a4c345d79f931df08c8acd391584595235295.jpg'),(15,'腌海螺',22.5,'大理洱海盛产螺蛳，白族人尤其喜欢食用螺蛳，自然吃的方法也很多，其中腌螺蛳就是一种独特的吃法。','../../images/food/352871da1a884c88b95adacd283022a71584595262509.jpg'),(16,'大理雕梅',16.8,'大理处于滇西交通要冲，有临近洱海的优势，稻作、水产、山珍皆有。其中雕梅、酸泡梨、话梅等腌制果脯远近闻名。','../../images/food/5664cb6b53b34891a923955620769d5a1584595286734.jpg'),(17,'五彩花米饭',12.8,'五彩花米饭是云南地区的特产，色彩鲜艳，味道悠远。色彩一般有红、紫、黑、白、黄等颜色，颜色鲜艳美观，称为“五彩花米饭”。','../../images/food/480f162f8f72482ba5f672249f9fecd71584595301500.jpg'),(18,'菠萝饭',12.8,'菠萝饭，又叫凤梨饭，营养全面，富含维生素及蛋白质，有助于提高记忆力;形式可爱，色彩丰富，香甜可口，在考试前夕能帮助精神压力大的孩子增加食欲、促进消化。','../../images/food/7c9d493788bc4b5390bcc514e6d7a19e1584595313198.jpg'),(19,'酸角糕',12.8,'酸甜味道符合大众口味，能促进肠胃消化，去除口气，可也清暑热，化积滞，治暑热食欲不振，妊娠呕吐，小儿疳积，治酒化为痰，解酒护肝，补钙。','../../images/food/1512f39c3d0a4d29b628574b184bf8f11584595398575.jpg'),(20,'鲜花饼',8.8,'鲜花饼是以云南特有的食用玫瑰花入料的酥饼，是具有云南特色的云南经典点心代表。','../../images/food/a23e4c5b20314c11aec1707b654d5e211584595413463.jpg');

/*Table structure for table `hotel` */

DROP TABLE IF EXISTS `hotel`;

CREATE TABLE `hotel` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(20) NOT NULL,
  `english` varchar(50) NOT NULL,
  `hotelDesc` varchar(100) NOT NULL,
  `location` varchar(50) NOT NULL,
  `price` double NOT NULL,
  `url` varchar(100) NOT NULL,
  `destId` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `destId` (`destId`),
  CONSTRAINT `hotel_ibfk_1` FOREIGN KEY (`destId`) REFERENCES `destination` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8;

